mapper.update:
update contact
set title=#{title}, content=#{content}
where id=#{id}

UPDATE contact
SET status = #{status}, answer = #{answer}
WHERE id = #{id}

mapper.select:
SELECT id, user_id, goods_id, title, content, status, answer, regdate, type
FROM contact
WHERE status = #{status}
ORDER BY regdate DESC
LIMIT #{limit} OFFSET #{offset}

SELECT * FROM contact
WHERE status = #{status} AND type = #{type}
ORDER BY regdate DESC
LIMIT #{limit} OFFSET #{offset}
